          }), _jsx(_components.span, {
            className: "katex-html",
            "aria-hidden": "true",
            children: _jsxs(_components.span, {
              className: "base",
              children: [_jsx(_components.span, {
                className: "strut",
                style: {
                  height: "1em",
                  verticalAlign: "-0.25em"
